sp_widget.client_script:
function($scope,$rootScope,spUtil,spModal) {
	/* widget controller */
	var c = this;

	$scope.actionText = 'Create Note';

	$rootScope.$on('selectNote', function(event,data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
			$scope.actionText = 'Update note';
		});
	});

	$rootScope.$on('deleteNote', function(event,id) {
		c.server.get({
			action: 'deleteNote',
			noteID: id
		}).then(function(r) {
			$rootScope.$emit('updateNotesList');
			spUtil.addTrivialMessage("Note deleted!");
			c.clearForm();
		});
	});

	c.clearForm = function() {
		c.data.noteID = null;
		c.data.title = null;
		c.data.note = null;
		$scope.actionText = 'Create note';
	}

	c.submitForm = function() {
		c.server.get({
			action: 'createOrUpdate',
			noteID: c.data.noteID,
			noteTitle: c.data.title,
			noteBody: c.data.note
		}).then(function(r) {
			$rootScope.$emit('updateNotesList', c.data.noteID);
			c.clearForm();
		});
	}

	c.confirmDelete = function() {
		spModal.open({
			title: "Delete Note",
			message: "Are you sure?",
			buttons: [
				{ label: '${No}', cancel: true},
				{ label: "${Yes}", primary: true}
			]
		}).then(function() {
			$rootScope.$emit('deleteNote', c.data.noteID);
		});
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	var note = new GlideRecord('x_58872_needit_note');
	if (input) {
		console.log(input.action + ": " + input.noteID);
		if (input.noteID && note.get(input.noteID)) {
			if (input.action == 'getNote') {
				console.log("In getNote function");
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id");
			}
			else if (input.action == 'createOrUpdate') {			
				if(input.noteID && input.noteID != null) {
					note.title = input.noteTitle; 
					note.note = input.noteBody; 
					note.update();
					console.log("In update function");
				}
			}
			else if (input.action == 'deleteNote') {
				console.log("In delete function");
				if(note.deleteRecord()) {
					return true;
				}
			}
		}
		else {
			console.log("Creating new");
			note.initialize();
			note.title = input.noteTitle; 
			note.note = input.noteBody;
			note.insert();
		}
	}

})();
