sp_widget.client_script:
function($rootScope,$scope,spModal,spUtil) {
	/* widget controller */
	var c = this;
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	c.deleteItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Deleting Note: ' + id);

		spModal.confirm("Are you sure?").then(function(confirmed) {
			if(confirmed) {
				$rootScope.$emit('deleteNote', id);
			}
		});
	}

	spUtil.recordWatch($scope, "x_58872_needit_note", "numberISNOTEMPTY", function(name) {
		console.log(name.data.operation); 
		console.log(name); 
		c.server.refresh();
	});

	$rootScope.$on('updateNotesList', function() { 
		c.server.update();
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	(function() {
		//create an array to populate without notes
		data.notes = [];

		var noteGR = new GlideRecord('x_58872_needit_note');
		noteGR.addQuery('user', gs.getUser().getID());
		noteGR.orderByDesc('sys_created_on');
		noteGR.setLimit(options.maximum_records_to_display);
		noteGR.query();

		while (noteGR.next()) {
			var noteObj = {};
			//use service portal helper method to get some display values
			$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
			//get the first 20 characters of the description
			noteObj.note = noteGR.getValue('note').slice(0,30);
			//push the populated obj into the array
			data.notes.push(noteObj);
		}
	})();
})();
